FlashBox objFlashBox = new FlashBox();
objFlashBox.Movie = "../../../../../Resources/Flash/FC_2_3_Column3D.swf";
objFlashBox.AddParameter("FlashVars","&dataURL=../../../../../Resources/Flash/FC_2_3_Column3D.xml");
objFlashBox.AddParameter("quality", "high");
objFlashBox.Dock = DockStyle.Fill;
this.Controls.Add(objFlashBox);

		
[System.ComponentModel.ToolboxItem(true)]
 public class MyWrappedWebControl : Gizmox.WebGUI.Forms.Hosts.AspControlBoxBase
 {
    /// <SUMMARY>
    /// Get the hosted control type
    /// </SUMMARY>
    protected override System.Type HostedControlType
    {
        get
        {
            return typeof(WebControl);
        }   
    }
 
    /// <SUMMARY>
    /// Get hosted control typed instance
    /// </SUMMARY>
    protected WebControl HostedASPxTreeList
    {
        get
        {
            return (WebControl)this.HostedControl;
        }
    }
 
}
